// Smart Start Handler
// Parses user input, handles resume requests, and initializes project

const chatInput = $input.first().json;
const userMessage = chatInput.chatInput || chatInput.message || '';
const chatHistory = chatInput.sessionId ? true : false;
const uploadedFiles = chatInput.files || [];

// Check for resume request
const isResumeRequest = userMessage.toLowerCase().includes('resume');

// Extract Google Drive folder ID from URL
let driveFolderId = null;
const driveUrlMatch = userMessage.match(/folders\/([a-zA-Z0-9_-]+)/);
if (driveUrlMatch) {
  driveFolderId = driveUrlMatch[1];
}

// Alternative: Check for direct folder ID
if (!driveFolderId) {
  const folderIdMatch = userMessage.match(/[a-zA-Z0-9_-]{25,}/);
  if (folderIdMatch && !folderIdMatch[0].includes(' ')) {
    driveFolderId = folderIdMatch[0];
  }
}

// Extract project name (look for quoted text or capitalized words)
let projectName = null;
const quotedMatch = userMessage.match(/["']([^"']+)["']/);
if (quotedMatch) {
  projectName = quotedMatch[1];
} else {
  // Look for "project name" or "called" patterns
  const namePatterns = [
    /project\s+(?:name|called|named|is)\s*[:\-]?\s*([A-Za-z0-9_-]+)/i,
    /called\s+([A-Za-z0-9_-]+)/i,
    /name\s*[:\-]\s*([A-Za-z0-9_-]+)/i
  ];

  for (const pattern of namePatterns) {
    const match = userMessage.match(pattern);
    if (match) {
      projectName = match[1];
      break;
    }
  }
}

// Generate session ID
const sessionId = 'session_' + Date.now() + '_' + Math.random().toString(36).substring(2, 8);

// Load configuration from environment
const config = {
  max_iterations: parseInt($env.FACTORY_MAX_ITERATIONS) || 5,
  score_threshold: parseInt($env.FACTORY_SCORE_THRESHOLD) || 90,
  batch_size: parseInt($env.FACTORY_BATCH_SIZE) || 3,
  confirmation_timeout: parseInt($env.FACTORY_CONFIRMATION_TIMEOUT) || 3600
};

return {
  json: {
    session_id: sessionId,
    user_message: userMessage,
    is_resume_request: isResumeRequest,
    drive_folder_id: driveFolderId,
    project_name: projectName,
    uploaded_files: uploadedFiles,
    has_drive_folder: !!driveFolderId,
    has_project_name: !!projectName,
    has_uploaded_files: uploadedFiles.length > 0,
    needs_more_info: !isResumeRequest && (!driveFolderId && uploadedFiles.length === 0),
    config,
    timestamp: new Date().toISOString()
  }
};

// Find most recent project with state file

const startData = $('Smart Start Handler').item.json;
const projects = $input.all().map(item => item.json);

// Filter for folders only and sort by modified time
const projectFolders = projects
  .filter(p => p.mimeType === 'application/vnd.google-apps.folder')
  .sort((a, b) => new Date(b.modifiedTime) - new Date(a.modifiedTime));

if (projectFolders.length === 0) {
  return {
    json: {
      ...startData,
      resume_available: false,
      message: "No existing projects found. Let's start a new one! Please provide:\n1. Google Drive Folder Link with context documents\n2. Project Name"
    }
  };
}

// Get the most recent project
const latestProject = projectFolders[0];

return {
  json: {
    ...startData,
    resume_available: true,
    latest_project: latestProject.name,
    latest_project_id: latestProject.id,
    latest_modified: latestProject.modifiedTime,
    all_projects: projectFolders.slice(0, 5).map(p => ({
      name: p.name,
      id: p.id,
      modified: p.modifiedTime
    }))
  }
};

// Parse loaded project state and prepare resume message

const prevData = $('Find Latest Project').item.json;
const downloadedFile = $input.first();

let stateContent = '';
if (downloadedFile.binary && downloadedFile.binary.data) {
  stateContent = Buffer.from(downloadedFile.binary.data.data, 'base64').toString('utf8');
}

let projectState = null;
try {
  projectState = JSON.parse(stateContent);
} catch (e) {
  projectState = null;
}

if (!projectState) {
  return {
    json: {
      ...prevData,
      has_valid_state: false,
      message: `Found project '${prevData.latest_project}' but couldn't load state. Would you like to start fresh?`
    }
  };
}

// Prepare resume information
const phaseNames = {
  0: 'Context Scavenging',
  1: 'Product Vision Loop',
  2: 'Architecture Loop',
  3: 'Final Audit'
};

const currentPhase = projectState.current_phase || 0;
const phaseName = phaseNames[currentPhase] || `Phase ${currentPhase}`;

const resumeMessage = `Found existing project: **${projectState.project_name}**

**Current Status**:
- Phase: ${currentPhase} - ${phaseName}
- Status: ${projectState.phase_status || 'in_progress'}
- Last Updated: ${projectState.updated_at || 'Unknown'}
- Total Iterations: ${projectState.telemetry?.total_iterations || 0}

Would you like to:
1. **Resume** from Phase ${currentPhase}
2. **Start over** with this project
3. Work on a **different project**`;

const webhookSuffix = `resume_choice_${prevData.session_id}`;

return {
  json: {
    ...prevData,
    has_valid_state: true,
    project_state: projectState,
    project_name: projectState.project_name,
    current_phase: currentPhase,
    phase_name: phaseName,
    resume_message: resumeMessage,
    webhook_suffix: webhookSuffix
  }
};

// Request more information from user

const data = $input.first().json;

let message = "I need a bit more information to get started.\n\n";

if (!data.has_drive_folder && !data.has_uploaded_files) {
  message += "Please provide context documents in one of these ways:\n";
  message += "- Paste a Google Drive folder link\n";
  message += "- Upload files directly in the chat\n\n";
}

if (!data.has_project_name) {
  message += "Also, what would you like to call this project?\n";
}

message += "\nExample: 'My project is called ShopFast. Here's the context folder: https://drive.google.com/drive/folders/abc123'";

return {
  json: {
    ...data,
    response_message: message,
    awaiting_input: true
  }
};

// Initialize new project

const data = $input.first().json;

// Generate project ID if no name provided
const projectId = data.project_name || 'Project_' + Date.now();

return {
  json: {
    session_id: data.session_id,
    project_id: projectId,
    project_name: projectId,
    drive_folder_id: data.drive_folder_id,
    uploaded_files: data.uploaded_files,
    config: data.config,
    current_phase: 0,
    phase_status: 'pending',
    is_new_project: true,
    timestamp: new Date().toISOString()
  }
};

// Create initial project state
// Store state in static data for later access after subworkflow calls

const prevData = $('Initialize New Project').item.json;
const folderResult = $input.first().json;

// Store state in static data for access after subworkflow calls
const staticData = $getWorkflowStaticData('global');
staticData.phase0InputState = null; // Will be set after this node

const projectState = {
  project_name: prevData.project_name,
  project_id: prevData.project_id,
  session_id: prevData.session_id,
  folder_id: folderResult.id,
  created_at: new Date().toISOString(),
  updated_at: new Date().toISOString(),
  current_phase: 0,
  phase_status: 'pending',
  last_iteration: {
    phase: 0,
    iteration: 0,
    score: 0
  },
  key_decisions: [],
  tech_standards: {
    global: [],
    local: []
  },
  artifacts: {
    vision_draft: null,
    architecture_draft: null,
    decision_log: null
  },
  telemetry: {
    total_iterations: 0,
    total_duration_ms: 0
  },
  config: prevData.config
};

return {
  json: {
    ...prevData,
    folder_id: folderResult.id,
    project_state: projectState,
    state_json: JSON.stringify(projectState, null, 2)
  }
};

// Store state in static data before Phase 0 subworkflow call
// This allows Update State After Phase 0 to access the state

const inputData = $input.first().json;

// Store in static data for access after subworkflow returns
const staticData = $getWorkflowStaticData('global');
staticData.phase0InputState = inputData;

// Pass through unchanged
return { json: inputData };

// Update state after Phase 0 completes
// Note: We cannot use $('Create Initial State') here as it's after a subworkflow call
// Instead, we use the static data pattern to preserve state

const staticData = $getWorkflowStaticData('global');
const prevState = staticData.phase0InputState || {};
const phase0Result = $input.first().json;

// Update project state
const updatedState = {
  ...prevState.project_state,
  current_phase: 1,
  phase_status: 'pending',
  updated_at: new Date().toISOString(),
  last_iteration: {
    phase: 0,
    iteration: phase0Result.summary?.total_standards_found || 0,
    score: 100 // Phase 0 doesn't have a score
  },
  tech_standards: {
    global: phase0Result.processed_standards?.filter(s => s.scope === 'global').map(s => s.name) || [],
    local: phase0Result.processed_standards?.filter(s => s.scope === 'local').map(s => s.name) || []
  },
  phase_0_result: phase0Result
};

const outputData = {
  ...prevState,
  project_state: updatedState,
  state_json: JSON.stringify(updatedState, null, 2),
  phase_0_complete: true,
  tech_standards: phase0Result.processed_standards || [],
  context: phase0Result.summary || {}
};

// Store state for Phase 1 to access after the subworkflow call
staticData.phase1InputState = outputData;

return { json: outputData };

// Update state after Phase 1 completes
// Note: We use static data to access state from before the subworkflow call

const staticData = $getWorkflowStaticData('global');
const prevData = staticData.phase1InputState || {};
const phase1Result = $input.first().json;

const updatedState = {
  ...prevData.project_state,
  current_phase: 2,
  phase_status: 'pending',
  updated_at: new Date().toISOString(),
  last_iteration: {
    phase: 1,
    iteration: phase1Result.total_iterations || 0,
    score: phase1Result.final_score || 0
  },
  artifacts: {
    ...prevData.project_state.artifacts,
    vision_draft: phase1Result.final_draft ? 'Drafts/ProductVision_FINAL.md' : null
  },
  telemetry: {
    ...prevData.project_state.telemetry,
    total_iterations: (prevData.project_state.telemetry?.total_iterations || 0) + (phase1Result.total_iterations || 0),
    total_duration_ms: (prevData.project_state.telemetry?.total_duration_ms || 0) + (phase1Result.telemetry?.duration_ms || 0)
  },
  phase_1_result: {
    status: phase1Result.status,
    final_score: phase1Result.final_score,
    total_iterations: phase1Result.total_iterations,
    convergence_achieved: phase1Result.convergence_achieved
  }
};

const outputData = {
  ...prevData,
  project_state: updatedState,
  state_json: JSON.stringify(updatedState, null, 2),
  vision_document: phase1Result.final_draft,
  vision_score: phase1Result.final_score,
  phase_1_complete: true
};

// Store state for Phase 2 to access after the subworkflow call
staticData.phase2InputState = outputData;

return { json: outputData };

// Finalize workflow and prepare output
// Use static data to access state from before the Phase 2 subworkflow call

const staticData = $getWorkflowStaticData('global');
const prevData = staticData.phase2InputState || {};
const phase2Result = $input.first().json;

const endTime = new Date();
const startTime = new Date(prevData.project_state?.created_at || endTime);
const totalDurationMs = endTime - startTime;
const totalDurationFormatted = `${Math.floor(totalDurationMs / 60000)}m ${Math.floor((totalDurationMs % 60000) / 1000)}s`;

const finalState = {
  ...prevData.project_state,
  current_phase: 3,
  phase_status: 'completed',
  updated_at: endTime.toISOString(),
  completed_at: endTime.toISOString(),
  artifacts: {
    ...prevData.project_state?.artifacts,
    architecture_draft: phase2Result.final_draft ? 'Drafts/Architecture_FINAL.md' : null
  },
  telemetry: {
    ...prevData.project_state?.telemetry,
    total_iterations: (prevData.project_state?.telemetry?.total_iterations || 0) + (phase2Result.total_iterations || 0),
    total_duration_ms: totalDurationMs,
    total_duration_formatted: totalDurationFormatted
  },
  phase_2_result: {
    status: phase2Result.status,
    final_score: phase2Result.final_score,
    total_iterations: phase2Result.total_iterations,
    convergence_achieved: phase2Result.convergence_achieved
  }
};

const completionMessage = `## AI Product Factory - Complete!

**Project**: ${prevData.project_name || 'Unknown'}

### Summary

| Phase | Status | Score | Iterations |
|-------|--------|-------|------------|
| Phase 0: Scavenging | Complete | N/A | ${prevData.project_state?.phase_0_result?.summary?.total_standards_found || 0} standards |
| Phase 1: Vision | ${prevData.phase_1_result?.status || 'Complete'} | ${prevData.vision_score || 'N/A'}/100 | ${prevData.phase_1_result?.total_iterations || 'N/A'} |
| Phase 2: Architecture | ${phase2Result.status} | ${phase2Result.final_score}/100 | ${phase2Result.total_iterations} |

### Artifacts Created
- **Product Vision**: Drafts/ProductVision_FINAL.md
- **Architecture**: Drafts/Architecture_FINAL.md
- **Decision Log**: decision_log.md

### Total Duration
${totalDurationFormatted}

All documents have been saved to your Google Drive in the **AI_Product_Factory/${prevData.project_name || 'Project'}** folder.`;

// CLEANUP: Clear static data to prevent state leakage between workflow runs
delete staticData.phase0InputState;
delete staticData.phase1InputState;
delete staticData.phase2InputState;

return {
  json: {
    project_id: prevData.project_id,
    project_name: prevData.project_name,
    session_id: prevData.session_id,
    status: 'completed',
    project_state: finalState,
    state_json: JSON.stringify(finalState, null, 2),
    vision_document: prevData.vision_document,
    architecture_document: phase2Result.final_draft,
    completion_message: completionMessage,
    telemetry: finalState.telemetry
  }
};

// Final Output - Return completion data to caller
// This node ensures all workflow output is properly structured

const data = $input.first().json;

return {
  json: {
    project_id: data.project_id,
    project_name: data.project_name,
    session_id: data.session_id,
    status: data.status || 'completed',
    completion_message: data.completion_message,
    vision_document: data.vision_document,
    architecture_document: data.architecture_document,
    project_state: data.project_state,
    telemetry: data.telemetry
  }
};

// No resume available - prompt for new project

const data = $input.first().json;

return {
  json: {
    ...data,
    response_message: data.message || "No existing projects found. Let's start a new one!\n\nPlease provide:\n1. A Google Drive folder link with your context documents\n2. Your project name\n\nExample: 'My project is called MyApp. Here's the folder: https://drive.google.com/drive/folders/abc123'",
    awaiting_input: true
  }
};

// Intermediate Output - Return response data for flows that need user input
// This handles resume prompts, info requests, and other intermediate states

const data = $input.first().json;

return {
  json: {
    project_id: data.project_id || null,
    session_id: data.session_id,
    status: data.awaiting_input ? 'awaiting_input' : 'intermediate',
    response_message: data.response_message || data.resume_message || data.message || 'Processing...',
    awaiting_input: data.awaiting_input || false,
    resume_available: data.resume_available || false,
    latest_project: data.latest_project || null,
    all_projects: data.all_projects || []
  }
};
