// S3 Storage Operations - Input Validator
// Validates incoming operation requests

const input = $input.first().json;

const operation = input.operation;
const validOperations = ['upload_artifact', 'download_artifact', 'list_artifacts', 'sync_to_postgres', 'create_bucket'];

if (!operation || !validOperations.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Valid operations: ${validOperations.join(', ')}`);
}

const projectId = input.project_id;
if (!projectId && operation !== 'create_bucket') {
  throw new Error('project_id is required for this operation');
}

return {
  json: {
    operation,
    project_id: projectId,
    artifact_type: input.artifact_type || 'unknown',
    content: input.content || '',
    filename: input.filename || '',
    content_type: input.content_type || 'text/markdown',
    s3_key: input.s3_key || '',
    project_state: input.project_state || null,
    bucket: $env.S3_BUCKET || 'product-factory-artifacts'
  }
};

// Build S3 key path for upload
const input = $input.first().json;

const projectId = input.project_id;
const artifactType = input.artifact_type;
const filename = input.filename;

let keyPath;
switch (artifactType) {
  case 'state':
    keyPath = `projects/${projectId}/state/${filename}`;
    break;
  case 'vision_final':
  case 'architecture_final':
  case 'decision_log':
    keyPath = `projects/${projectId}/artifacts/${filename}`;
    break;
  case 'iteration':
    const phase = input.phase || 'vision';
    const iteration = input.iteration || 1;
    keyPath = `projects/${projectId}/iterations/${phase}/v${iteration}/${filename}`;
    break;
  case 'standards':
    keyPath = `projects/${projectId}/standards/${filename}`;
    break;
  default:
    keyPath = `projects/${projectId}/${filename}`;
}

return {
  json: {
    ...input,
    s3_key: keyPath
  }
};

// Build upload response
const input = $input.first().json;
const httpResponse = $('S3 Upload').first().json;

const success = httpResponse.statusCode >= 200 && httpResponse.statusCode < 300;

return {
  json: {
    operation: 'upload_artifact',
    success,
    s3_key: input.s3_key,
    bucket: input.bucket,
    project_id: input.project_id,
    artifact_type: input.artifact_type,
    status_code: httpResponse.statusCode,
    message: success ? 'Upload successful' : `Upload failed: ${httpResponse.statusCode}`
  }
};

// Build S3 key for download
const input = $input.first().json;

let keyPath = input.s3_key;
if (!keyPath && input.filename) {
  keyPath = `projects/${input.project_id}/artifacts/${input.filename}`;
}

return {
  json: {
    ...input,
    s3_key: keyPath
  }
};

// Build download response
const input = $input.first().json;
const httpResponse = $('S3 Download').first().json;

const success = httpResponse.statusCode >= 200 && httpResponse.statusCode < 300;

return {
  json: {
    operation: 'download_artifact',
    success,
    s3_key: input.s3_key,
    content: success ? httpResponse.data : null,
    project_id: input.project_id,
    status_code: httpResponse.statusCode,
    message: success ? 'Download successful' : `Download failed: ${httpResponse.statusCode}`
  }
};

// Parse S3 list response (XML)
const input = $input.first().json;
const response = $('S3 List Objects').first().json;

let objects = [];

if (response.statusCode >= 200 && response.statusCode < 300) {
  // Simple XML parsing for S3 list response
  const xml = response.data || '';
  const keyMatches = xml.matchAll(/<Key>([^<]+)<\/Key>/g);
  const sizeMatches = xml.matchAll(/<Size>([^<]+)<\/Size>/g);
  const dateMatches = xml.matchAll(/<LastModified>([^<]+)<\/LastModified>/g);

  const keys = [...keyMatches].map(m => m[1]);
  const sizes = [...sizeMatches].map(m => parseInt(m[1]));
  const dates = [...dateMatches].map(m => m[1]);

  objects = keys.map((key, i) => ({
    key,
    size: sizes[i] || 0,
    lastModified: dates[i] || null,
    name: key.split('/').pop()
  }));
}

return {
  json: {
    operation: 'list_artifacts',
    success: response.statusCode >= 200 && response.statusCode < 300,
    project_id: input.project_id,
    objects,
    count: objects.length
  }
};

// Build PostgreSQL upsert query
const input = $input.first().json;
const state = input.project_state;

if (!state) {
  return {
    json: {
      operation: 'sync_to_postgres',
      success: false,
      message: 'project_state is required'
    }
  };
}

return {
  json: {
    ...input,
    query_params: {
      project_id: state.project_id || input.project_id,
      project_name: state.project_name || 'Unknown',
      session_id: state.session_id || null,
      current_phase: state.current_phase || 0,
      phase_status: state.phase_status || 'pending',
      last_iteration_score: state.last_iteration_score || null,
      tech_standards_global: JSON.stringify(state.tech_standards_global || []),
      tech_standards_local: JSON.stringify(state.tech_standards_local || []),
      total_iterations: state.total_iterations || 0,
      total_duration_ms: state.total_duration_ms || 0,
      config: JSON.stringify(state.config || {}),
      full_state: JSON.stringify(state)
    }
  }
};

// Build PostgreSQL sync response
const input = $input.first().json;

return {
  json: {
    operation: 'sync_to_postgres',
    success: true,
    project_id: input.query_params?.project_id || input.project_id,
    message: 'Project state synced to PostgreSQL'
  }
};
