// Error Handler - Log workflow errors for centralized handling
// This node captures any workflow errors and formats them for logging

const error = $input.first().json;

const errorDetails = {
  timestamp: new Date().toISOString(),
  workflow: 'Titan - Adversarial Agent Loop',
  error_type: error.execution?.error?.name || 'UnknownError',
  error_message: error.execution?.error?.message || 'No error message available',
  error_stack: error.execution?.error?.stack || '',
  execution_id: error.execution?.id || 'unknown',
  node_name: error.execution?.lastNodeExecuted || 'unknown',
  mode: error.execution?.mode || 'unknown',
  retry_of: error.execution?.retryOf || null,
  severity: 'ERROR',
  recoverable: false
};

// Log to console for n8n logs
console.error('Titan Adversarial Loop Error:', JSON.stringify(errorDetails, null, 2));

return {
  json: errorDetails
};

// Validate required input fields
const input = $input.first().json;

if (!input.task) {
  throw new Error('Missing required field: task');
}

if (!input.creator_prompt) {
  throw new Error('Missing required field: creator_prompt');
}

if (!input.critic_prompt) {
  throw new Error('Missing required field: critic_prompt');
}

// Generate a unique session ID using UUID v4 format
const generateSessionId = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, (c) => {
    const r = Math.random() * 16 | 0;
    return (c === 'x' ? r : (r & 0x3 | 0x8)).toString(16);
  });
};

const sessionId = generateSessionId();

// Enhanced sanitization to prevent prompt injection and XSS
const sanitizeInput = (text) => {
  if (!text) return '';
  return text
    .replace(/\{\{/g, '{ {')           // n8n template injection
    .replace(/\}\}/g, '} }')           // n8n template injection
    .replace(/\$\(/g, '$ (')           // Shell command substitution
    .replace(/`/g, "'")                // Backtick (template literals/shell)
    .replace(/\$\{/g, '$ {')           // Template literal injection
    .replace(/<script[^>]*>/gi, '')    // XSS script tags
    .replace(/<\/script>/gi, '')       // XSS script closing tags
    .replace(/javascript:/gi, '')      // JavaScript protocol
    .replace(/on\w+\s*=/gi, '')        // Event handler injection
    .trim();
};

return {
  json: {
    iteration: 0,
    score: 0,
    max_iterations: input.max_iterations || 5,
    score_threshold: input.score_threshold || 9,
    draft: '',
    context: sanitizeInput(input.context || ''),
    task: sanitizeInput(input.task || ''),
    creator_prompt: input.creator_prompt || '',
    critic_prompt: input.critic_prompt || '',
    refiner_prompt: input.refiner_prompt || input.creator_prompt || '',
    session_id: sessionId,
    start_time: new Date().toISOString(),
    iteration_history: []
  }
};

// Set draft from creator and increment iteration
const agentOutput = $input.first().json;
const initState = $('Loop State Initializer').item.json;

const draft = agentOutput.output || agentOutput.text || '';
const currentIteration = initState.iteration + 1;

return {
  json: {
    ...initState,
    draft: draft,
    iteration: currentIteration,
    last_action: 'creator',
    iteration_history: [
      ...initState.iteration_history,
      {
        iteration: currentIteration,
        action: 'creator',
        timestamp: new Date().toISOString(),
        draft_length: draft.length,
        draft_content: draft,
        filename: `draft_iteration_${currentIteration}.md`
      }
    ]
  }
};

// Parse the critic's response and update state
const response = $input.first().json;
const criticContent = response.output || response.text || '';

// Get previous state - this comes from either Draft State Updater or Refined Draft State Updater
const inputData = $('Critic - Quality Evaluator').first().json;

let prevState;
try {
  prevState = $('Refined Draft State Updater').first()?.json;
} catch (e) {
  prevState = null;
}

if (!prevState || !prevState.draft) {
  try {
    prevState = $('Draft State Updater').first()?.json;
  } catch (e) {
    prevState = null;
  }
}

if (!prevState || !prevState.draft) {
  prevState = $('Loop State Initializer').first().json;
}

// Enhanced JSON parsing with multiple fallback strategies
let evaluation;
let parseMethod = 'direct';

// Strategy 1: Direct JSON parse
try {
  evaluation = JSON.parse(criticContent);
} catch (e) {
  // Strategy 2: Extract JSON from markdown code block
  const codeBlockMatch = criticContent.match(/```(?:json)?\s*([\s\S]*?)```/);
  if (codeBlockMatch) {
    try {
      evaluation = JSON.parse(codeBlockMatch[1].trim());
      parseMethod = 'code_block';
    } catch (e2) { evaluation = null; }
  }

  // Strategy 3: Extract any JSON object
  if (!evaluation) {
    const jsonMatch = criticContent.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      try {
        evaluation = JSON.parse(jsonMatch[0]);
        parseMethod = 'regex_extract';
      } catch (e3) { evaluation = null; }
    }
  }

  // Strategy 4: Extract score from text patterns
  if (!evaluation) {
    const scorePatterns = [
      /score[:\s]+([\d.]+)/i,
      /rating[:\s]+([\d.]+)/i,
      /([\d.]+)\s*\/\s*10/,
      /grade[:\s]+([\d.]+)/i
    ];
    let extractedScore = null;
    for (const pattern of scorePatterns) {
      const match = criticContent.match(pattern);
      if (match) {
        extractedScore = parseFloat(match[1]);
        break;
      }
    }

    evaluation = {
      score: extractedScore || 5,
      issues: ['Response was not in expected JSON format'],
      strengths: [],
      recommendations: ['Regenerate critic response or review manually'],
      raw_response: criticContent.substring(0, 500)
    };
    parseMethod = 'text_extraction';
  }
}

// Ensure score is a valid number between 0-10
let score = typeof evaluation.score === 'number' ? evaluation.score : parseFloat(evaluation.score);
if (isNaN(score)) score = 5;
score = Math.max(0, Math.min(10, score));

return {
  json: {
    ...prevState,
    score: score,
    evaluation: evaluation,
    critic_feedback: criticContent,
    last_action: 'critic',
    iteration_history: [
      ...(prevState.iteration_history || []),
      {
        iteration: prevState.iteration,
        action: 'critic',
        timestamp: new Date().toISOString(),
        score: score,
        issues_count: (evaluation.issues || []).length,
        feedback_content: criticContent,
        evaluation: evaluation,
        filename: `critic_feedback_iteration_${prevState.iteration}.md`
      }
    ]
  }
};

// Set refined draft and increment iteration for next loop
const agentOutput = $input.first().json;
const prevState = $('Critic Response Parser').item.json;

const refinedDraft = agentOutput.output || agentOutput.text || '';
const newIteration = prevState.iteration + 1;

return {
  json: {
    ...prevState,
    draft: refinedDraft,
    iteration: newIteration,
    last_action: 'refiner',
    iteration_history: [
      ...(prevState.iteration_history || []),
      {
        iteration: newIteration,
        action: 'refiner',
        timestamp: new Date().toISOString(),
        draft_length: refinedDraft.length,
        previous_score: prevState.score,
        draft_content: refinedDraft,
        filename: `refined_draft_iteration_${newIteration}.md`
      }
    ]
  }
};

// Prepare success output with telemetry
const state = $input.first().json;
const startTime = new Date(state.start_time || Date.now());
const endTime = new Date();
const durationMs = endTime - startTime;

return {
  json: {
    status: 'success',
    final_draft: state.draft,
    final_score: state.score,
    total_iterations: state.iteration,
    session_id: state.session_id,
    telemetry: {
      start_time: state.start_time,
      end_time: endTime.toISOString(),
      duration_ms: durationMs,
      duration_formatted: `${Math.floor(durationMs / 60000)}m ${Math.floor((durationMs % 60000) / 1000)}s`,
      iteration_history: state.iteration_history,
      final_evaluation: state.evaluation
    }
  }
};

// Prepare circuit breaker - max iterations reached, prepare for human guidance
const state = $input.first().json;

// Extract persistent issues
const issues = state.evaluation?.issues || [];
const criticalIssues = issues.filter(i => i.severity === 'high' || i.severity === 'critical');
const majorIssues = issues.filter(i => i.severity === 'major' || i.severity === 'medium');

const issuesSummary = [
  ...criticalIssues.map(i => `[CRITICAL] ${i.issue || i.detail || i.section}`),
  ...majorIssues.map(i => `[MAJOR] ${i.issue || i.detail || i.section}`)
].slice(0, 5).join('\n');

const escalationMessage = `The draft has gone through ${state.max_iterations} iterations but hasn't reached the quality threshold.\n\n**Current Score**: ${state.score}/10 (threshold: ${state.score_threshold})\n\n**Persistent Issues**:\n${issuesSummary || 'See detailed evaluation for issues'}\n\n**Options**:\n1. Accept current version (score: ${state.score})\n2. Provide specific guidance on issues\n3. Lower quality threshold\n4. Start over with different approach`;

const webhookSuffix = `circuit_breaker_${state.session_id}`;

return {
  json: {
    ...state,
    escalation_message: escalationMessage,
    webhook_suffix: webhookSuffix,
    status: 'awaiting_human_guidance'
  }
};

// Process human response to circuit breaker
const prevState = $('Circuit Breaker Prep').item.json;
const webhookData = $input.first().json;

// Parse user response
// Expected: { "action": "accept|guidance|lower_threshold|restart", "value": "..." }
const userAction = webhookData.body?.action || webhookData.action || 'accept';
const userValue = webhookData.body?.value || webhookData.value || '';

const startTime = new Date(prevState.start_time || Date.now());
const endTime = new Date();
const durationMs = endTime - startTime;

let result = {
  status: 'human_decision',
  final_draft: prevState.draft,
  final_score: prevState.score,
  total_iterations: prevState.iteration,
  session_id: prevState.session_id,
  human_action: userAction,
  human_value: userValue,
  telemetry: {
    start_time: prevState.start_time,
    end_time: endTime.toISOString(),
    duration_ms: durationMs,
    duration_formatted: `${Math.floor(durationMs / 60000)}m ${Math.floor((durationMs % 60000) / 1000)}s`,
    iteration_history: prevState.iteration_history,
    final_evaluation: prevState.evaluation,
    human_intervention: true
  }
};

switch (userAction) {
  case 'accept':
    result.status = 'accepted_by_human';
    result.message = `Draft accepted by human at score ${prevState.score}/${prevState.score_threshold}`;
    break;
  case 'guidance':
    result.status = 'guidance_provided';
    result.additional_context = userValue;
    result.message = 'Human provided additional guidance';
    break;
  case 'lower_threshold':
    const newThreshold = parseInt(userValue) || prevState.score_threshold - 1;
    result.new_threshold = Math.max(5, newThreshold);
    result.status = 'threshold_lowered';
    result.message = `Quality threshold lowered to ${result.new_threshold}`;
    break;
  case 'restart':
    result.status = 'restart_requested';
    result.message = 'Human requested restart with new approach';
    break;
  default:
    result.status = 'accepted_by_human';
    result.message = 'Draft accepted (default action)';
}

return { json: result };
